// Reducer for state management
const appReducer = (state, action) => {
  switch (action.type) {
    case 'SET_LOADING':
      return {
        ...state,
        loading: action.payload
      };
    case 'SET_ERROR':
      return {
        ...state,
        error: action.payload,
        loading: false
      };
    case 'CLEAR_ERROR':
      return {
        ...state,
        error: null
      };
    case 'TOGGLE_AUTH':
      return {
        ...state,
        isAuthenticated: action.payload
      };
    case 'SET_AUTH_MODE':
      return {
        ...state,
        authMode: action.payload
      };
    case 'SET_VIEW':
      return {
        ...state,
        currentView: action.payload
      };
    case 'SET_USER':
      return {
        ...state,
        user: action.payload
      };
    case 'SET_ACTIVITIES':
      return {
        ...state,
        activities: action.payload
      };
    case 'ADD_ACTIVITY':
      return {
        ...state,
        activities: [action.payload, ...state.activities],
        user: {
          ...state.user,
          points: state.user.points + action.payload.points
        }
      };
    case 'SET_LEADERBOARD':
      return {
        ...state,
        leaderboard: action.payload
      };
    case 'SET_WEATHER':
      return {
        ...state,
        weatherData: action.payload
      };
    case 'UPDATE_USER_GOAL':
      return {
        ...state,
        user: {
          ...state.user,
          target_kg: action.payload
        }
      };
    case 'LOGOUT':
      authAPI.logout();
      return {
        ...initialState,
        isAuthenticated: false,
        weatherData: state.weatherData
      };
    default:
      return state;
  }
};

