import React, { useState, useReducer, useEffect } from 'react';
import { BookOpen, Home, Leaf, Twitter, Linkedin, Share2, TrendingUp, Award, Zap, Car, Lightbulb, UtensilsCrossed, Plus, Target, Users, Calendar } from 'lucide-react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { authAPI, activitiesAPI, leaderboardAPI, isAuthenticated } from './services/api';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

// Initial state for the app
const initialState = {
  isAuthenticated: isAuthenticated(),
  currentView: 'dashboard', // 'dashboard', 'resources', 'activities'
  authMode: 'idle', // 'idle', 'login', 'register'
  user: {
    id: '',
    email: '',
    points: 0,
    target_kg: 50,
    registered_on: ''
  },
  activities: [],
  weatherData: { condition: 'Sunny', temp: '25°C' },
  leaderboard: [],
  loading: false,
  error: null
};

// Reducer for state management
const appReducer = (state, action) => {
  switch (action.type) {
    case 'SET_LOADING':
      return { ...state, loading: action.payload };
    case 'SET_ERROR':
      return { ...state, error: action.payload, loading: false };
    case 'CLEAR_ERROR':
      return { ...state, error: null };
    case 'TOGGLE_AUTH':
      return { ...state, isAuthenticated: action.payload };
    case 'SET_AUTH_MODE':
      return { ...state, authMode: action.payload };
    case 'SET_VIEW':
      return { ...state, currentView: action.payload };
    case 'SET_USER':
      return { ...state, user: action.payload };
    case 'SET_ACTIVITIES':
      return { ...state, activities: action.payload };
    case 'ADD_ACTIVITY':
      return { 
        ...state, 
        activities: [action.payload, ...state.activities],
        user: { ...state.user, points: state.user.points + action.payload.points }
      };
    case 'SET_LEADERBOARD':
      return { ...state, leaderboard: action.payload };
    case 'SET_WEATHER':
      return { ...state, weatherData: action.payload };
    case 'UPDATE_USER_GOAL':
      return { ...state, user: { ...state.user, target_kg: action.payload } };
    case 'LOGOUT':
      authAPI.logout();
      return {
        ...initialState,
        isAuthenticated: false,
        weatherData: state.weatherData
      };
    default:
      return state;
  }
};

// Mock weather data generator
const generateWeatherData = () => {
  const conditions = [
    { condition: 'Sunny', temp: '25°C' },
    { condition: 'Rainy', temp: '15°C' }
  ];
  return conditions[Math.floor(Math.random() * conditions.length)];
};

// Carbon emission factors
const EMISSION_FACTORS = {
  transport: {
    'Sedan': 0.2,
    'SUV': 0.3,
    'Motorbike': 0.1
  },
  energy: 0.4, // kg CO2 per kWh
  food: {
    'High Meat': 5.0,
    'Low Meat': 3.0,
    'Vegetarian': 1.5,
    'Vegan': 0.5
  }
};

// AuthLandingPage Component
const AuthLandingPage = ({ state, dispatch }) => {
  const [formData, setFormData] = useState({ email: '', password: '', confirmPassword: '' });
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'CLEAR_ERROR' });

    try {
      await authAPI.login(formData.email, formData.password);
      const userProfile = await authAPI.getProfile();

      dispatch({ type: 'SET_USER', payload: userProfile });
      dispatch({ type: 'TOGGLE_AUTH', payload: true });
      dispatch({ type: 'SET_AUTH_MODE', payload: 'idle' });
      setError('');
    } catch (error) {
      setError(error.message);
      dispatch({ type: 'SET_ERROR', payload: error.message });
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'CLEAR_ERROR' });

    try {
      await authAPI.register(formData.email, formData.password);
      setError('');
      dispatch({ type: 'SET_AUTH_MODE', payload: 'login' });
      setFormData({ email: formData.email, password: '', confirmPassword: '' });
      // Show success message
      setError('Registration successful! Please log in.');
    } catch (error) {
      setError(error.message);
      dispatch({ type: 'SET_ERROR', payload: error.message });
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError('');
  };

  if (state.authMode === 'login' || state.authMode === 'register') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-400 via-green-500 to-green-600 flex items-center justify-center px-4">
        <div className="max-w-md w-full bg-white rounded-2xl shadow-2xl p-8">
          <div className="text-center mb-6">
            <h2 className="text-3xl font-bold text-gray-800 mb-2">
              {state.authMode === 'login' ? 'Welcome Back' : 'Join EcoTrack'}
            </h2>
            <p className="text-gray-600">
              {state.authMode === 'login' 
                ? 'Sign in to continue your eco journey' 
                : 'Start tracking your carbon footprint today'
              }
            </p>
          </div>

          {error && (
            <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded-lg">
              {error}
            </div>
          )}

          <form onSubmit={state.authMode === 'login' ? handleLogin : handleRegister} className="space-y-4">
            <div>
              <label className="block text-gray-700 text-sm font-semibold mb-2">
                Email Address
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent transition duration-200"
                placeholder="Enter your email"
                required
              />
            </div>

            <div>
              <label className="block text-gray-700 text-sm font-semibold mb-2">
                Password
              </label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent transition duration-200"
                placeholder="Enter your password"
                required
              />
            </div>

            {state.authMode === 'register' && (
              <div>
                <label className="block text-gray-700 text-sm font-semibold mb-2">
                  Confirm Password
                </label>
                <input
                  type="password"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent transition duration-200"
                  placeholder="Confirm your password"
                  required
                />
              </div>
            )}

            <button
              type="submit"
              className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-3 px-4 rounded-lg transition duration-200"
            >
              {state.authMode === 'login' ? 'Sign In' : 'Create Account'}
            </button>
          </form>

          <div className="mt-6 text-center">
            <button
              onClick={() => dispatch({ type: 'SET_AUTH_MODE', payload: 'idle' })}
              className="text-green-600 hover:text-green-700 font-medium transition duration-200"
            >
              ← Back to main page
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-400 via-green-500 to-green-600 flex items-center justify-center px-4">
      <div className="max-w-4xl w-full text-center">
        {/* Logo and Title */}
        <div className="mb-8">
          <div className="flex justify-center mb-4">
            <div className="bg-white rounded-full p-4 shadow-lg">
              <Leaf className="w-16 h-16 text-green-600" />
            </div>
          </div>
          <h1 className="text-6xl md:text-7xl font-bold text-white mb-4 tracking-tight">
            EcoTrack
          </h1>
          <p className="text-xl md:text-2xl text-green-100 font-light">
            Track. Reduce. Impact.
          </p>
        </div>

        {/* Call to Action Buttons */}
        <div className="flex flex-col sm:flex-row gap-4 justify-center items-center mb-12">
          <button
            onClick={() => dispatch({ type: 'SET_AUTH_MODE', payload: 'login' })}
            className="bg-white text-green-600 font-semibold py-4 px-8 rounded-xl shadow-lg hover:shadow-xl transform hover:scale-105 transition duration-300 min-w-[200px]"
          >
            Login
          </button>
          <button
            onClick={() => dispatch({ type: 'SET_AUTH_MODE', payload: 'register' })}
            className="bg-green-800 text-white font-semibold py-4 px-8 rounded-xl shadow-lg hover:shadow-xl transform hover:scale-105 transition duration-300 min-w-[200px] border-2 border-green-700 hover:border-green-600"
          >
            Create Account
          </button>
        </div>

        {/* About Section */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 text-white">
          <div className="bg-white bg-opacity-10 backdrop-blur-sm rounded-xl p-6 border border-white border-opacity-20">
            <div className="text-3xl font-bold mb-2">🌱</div>
            <h3 className="font-semibold mb-2">Track Daily Activities</h3>
            <p className="text-green-100 text-sm">Monitor transportation, energy usage, and meals</p>
          </div>
          <div className="bg-white bg-opacity-10 backdrop-blur-sm rounded-xl p-6 border border-white border-opacity-20">
            <div className="text-3xl font-bold mb-2">🤖</div>
            <h3 className="font-semibold mb-2">AI-Powered Analysis</h3>
            <p className="text-green-100 text-sm">Get personalized recommendations to reduce your impact</p>
          </div>
          <div className="bg-white bg-opacity-10 backdrop-blur-sm rounded-xl p-6 border border-white border-opacity-20">
            <div className="text-3xl font-bold mb-2">🏆</div>
            <h3 className="font-semibold mb-2">Compete & Achieve</h3>
            <p className="text-green-100 text-sm">Join the leaderboard and earn eco-points</p>
          </div>
          <div className="bg-white bg-opacity-10 backdrop-blur-sm rounded-xl p-6 border border-white border-opacity-20">
            <div className="text-3xl font-bold mb-2">📊</div>
            <h3 className="font-semibold mb-2">Real-time Insights</h3>
            <p className="text-green-100 text-sm">Compare your footprint with regional averages</p>
          </div>
        </div>
      </div>
    </div>
  );
};

// ActivityLogView Component
const ActivityLogView = ({ state, dispatch }) => {
  const [activeForm, setActiveForm] = useState('transport');
  const [formData, setFormData] = useState({
    transport: { vehicleType: 'Sedan', distance: '' },
    energy: { electricity: '' },
    food: { mealType: 'High Meat' }
  });

  const calculateEmission = (type, data) => {
    switch (type) {
      case 'transport':
        return EMISSION_FACTORS.transport[data.vehicleType] * parseFloat(data.distance || 0);
      case 'energy':
        return EMISSION_FACTORS.energy * parseFloat(data.electricity || 0);
      case 'food':
        return EMISSION_FACTORS.food[data.mealType];
      default:
        return 0;
    }
  };

  const handleSubmit = async (type) => {
    const data = formData[type];

    // Validate form data
    if (type === 'transport' && !data.distance) return;
    if (type === 'energy' && !data.electricity) return;

    dispatch({ type: 'SET_LOADING', payload: true });

    try {
      const activity = await activitiesAPI.logActivity(type, data);
      dispatch({ type: 'ADD_ACTIVITY', payload: activity });

      // Reset form
      setFormData({
        ...formData,
        [type]: type === 'transport' ? { vehicleType: 'Sedan', distance: '' } :
                type === 'energy' ? { electricity: '' } :
                { mealType: 'High Meat' }
      });
    } catch (error) {
      dispatch({ type: 'SET_ERROR', payload: error.message });
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  };

  const updateFormData = (type, field, value) => {
    setFormData({
      ...formData,
      [type]: { ...formData[type], [field]: value }
    });
  };

  const renderTransportForm = () => (
    <div className="space-y-4">
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">Vehicle Type</label>
        <select
          value={formData.transport.vehicleType}
          onChange={(e) => updateFormData('transport', 'vehicleType', e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500"
        >
          <option value="Sedan">Sedan</option>
          <option value="SUV">SUV</option>
          <option value="Motorbike">Motorbike</option>
        </select>
      </div>
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">Distance (km)</label>
        <input
          type="number"
          value={formData.transport.distance}
          onChange={(e) => updateFormData('transport', 'distance', e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500"
          placeholder="Enter distance"
        />
      </div>
      {formData.transport.distance && (
        <div className="p-3 bg-green-50 rounded-lg">
          <p className="text-sm text-gray-600">Estimated Carbon Emission:</p>
          <p className="text-lg font-semibold text-green-600">
            {calculateEmission('transport', formData.transport).toFixed(2)} kg CO₂
          </p>
        </div>
      )}
      <button
        onClick={() => handleSubmit('transport')}
        disabled={!formData.transport.distance}
        className="w-full bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 disabled:bg-gray-300 transition duration-200"
      >
        Log Transport Activity
      </button>
    </div>
  );

  const renderEnergyForm = () => (
    <div className="space-y-4">
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">Electricity Used (kWh)</label>
        <input
          type="number"
          value={formData.energy.electricity}
          onChange={(e) => updateFormData('energy', 'electricity', e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500"
          placeholder="Enter electricity usage"
        />
      </div>
      {formData.energy.electricity && (
        <div className="p-3 bg-green-50 rounded-lg">
