          onClick={() => handleShare('linkedin')}
          className="flex items-center space-x-2 bg-blue-700 text-white px-4 py-2 rounded-lg hover:bg-blue-800 transition duration-200"
        >
          <Linkedin className="w-4 h-4" />
          <span>LinkedIn</span>
        </button>
