    </div>
  );
};

// CarbonChart Component
const CarbonChart = ({ activities }) => {
  // Generate last 7 days data
  const last7Days = Array.from({ length: 7 }, (_, i) => {
    const date = new Date();
    date.setDate(date.getDate() - (6 - i));
    return date.toLocaleDateString('en-US', { weekday: 'short' });
  });

  const chartData = last7Days.map(day => {
    const dayActivities = activities.filter(activity => {
      const activityDate = new Date(activity.timestamp);
      const dayName = activityDate.toLocaleDateString('en-US', { weekday: 'short' });
      return dayName === day;
    });
    return dayActivities.reduce((sum, activity) => sum + activity.carbon_kg, 0);
  });

  const data = {
    labels: last7Days,
    datasets: [
      {
        label: 'Carbon Footprint (kg CO₂)',
        data: chartData,
        backgroundColor: 'rgba(34, 197, 94, 0.6)',
        borderColor: 'rgba(34, 197, 94, 1)',
        borderWidth: 2,
        borderRadius: 4,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Weekly Carbon Footprint Trend',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'kg CO₂'
        }
      },
    },
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <Bar data={data} options={options} />
    </div>
  );
};

