import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './context/AuthContext';

// Pages
import Dashboard from './pages/Dashboard';
import Leaderboard from './pages/Leaderboard';
import ResourceHub from './pages/ResourceHub';

// Components
import Navbar from './components/Navbar';
import AuthLandingPage from './components/AuthLandingPage';

// Protected Route Component
const ProtectedRoute = ({ children }) => {
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated) {
    return <AuthLandingPage />;
  }

  return children;
};

// Public Route Component (for authenticated users to access public pages)
const PublicRoute = ({ children }) => {
  const { isAuthenticated } = useAuth();

  return (
    <div className="min-h-screen bg-gray-50">
      {isAuthenticated && <Navbar />}
      <div className={isAuthenticated ? "container mx-auto px-4 py-8" : ""}>
        {children}
      </div>
    </div>
  );
};

function App() {
  const { isAuthenticated, loading } = useAuth();

  // Show loading spinner while checking authentication
  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-400 via-green-500 to-green-600 flex items-center justify-center">
        <div className="bg-white rounded-full p-4 shadow-lg">
          <svg className="animate-spin h-8 w-8 text-green-600" fill="none" viewBox="0 0 24 24">
            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
          </svg>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <Routes>
        {/* Landing/Auth Route */}
        <Route 
          path="/auth" 
          element={
            isAuthenticated ? <Navigate to="/" /> : <AuthLandingPage />
          } 
        />

        {/* Protected Routes */}
        <Route 
          path="/" 
          element={
            <ProtectedRoute>
              <PublicRoute>
                <Dashboard />
              </PublicRoute>
            </ProtectedRoute>
          } 
        />

        <Route 
          path="/resources" 
          element={
            <ProtectedRoute>
              <PublicRoute>
                <ResourceHub />
              </PublicRoute>
            </ProtectedRoute>
          } 
        />

        {/* Public Routes (accessible to authenticated users) */}
        <Route 
          path="/leaderboard" 
          element={
            <PublicRoute>
              <Leaderboard />
            </PublicRoute>
          } 
        />

        {/* Redirect all other routes */}
        <Route 
          path="*" 
          element={
            isAuthenticated ? <Navigate to="/" /> : <Navigate to="/auth" />
          } 
        />
      </Routes>
    </Router>
  );
}

export default App;
